    private void button1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_button1ActionPerformed
        // TODO add your handling code here:
        String taskName = textField1.getText();
        if(checkbox1.getState() == true){
            taskName = taskName + " (High Priority)";
        }
        TodoList.add(taskName);
        textField1.setText("");
    }//GEN-LAST:event_button1ActionPerformed

    private void button2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_button2ActionPerformed
        // TODO add your handling code here:
        if (TodoList.getSelectedIndex() != -1) {
            TodoList.remove(TodoList.getSelectedIndex());
        }
        if (FinishedList.getSelectedIndex() != -1) {
            FinishedList.remove(FinishedList.getSelectedIndex());
        }
    }//GEN-LAST:event_button2ActionPerformed

    private void button4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_button4ActionPerformed
        if (TodoList.getSelectedIndex() != -1) {
            FinishedList.add(TodoList.getSelectedItem());
            TodoList.remove(TodoList.getSelectedIndex());
        }
        
    }//GEN-LAST:event_button4ActionPerformed

    private void button3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_button3ActionPerformed
        // TODO add your handling code here:
        
        if (TodoList.getSelectedIndex() != -1) {
            TodoList.removeAll();
        }
        if (FinishedList.getSelectedIndex() != -1) {
            FinishedList.removeAll();
        }
    }//GEN-LAST:event_button3ActionPerformed

