    private void btnDisplayTextActionPerformed(java.awt.event.ActionEvent evt) {                                               
        // TODO add your handling code here:
        String result = "";
        
        for(int i = 0; i < Integer.parseInt(tfNumber.getText()); i++){
            if(i < Integer.parseInt(tfNumber.getText()) - 1)
                result += tfText.getText() + " ";
            else
                result += tfText.getText();
        }
        
        txtResult.setText(result);
    }                                              

