    private void btnComputeActionPerformed(java.awt.event.ActionEvent evt) {                                           
        // TODO add your handling code here:

        if(operationsComboBox.getItemAt(operationsComboBox.getSelectedIndex()).equals("+")){
            resultLabel.setText((Integer.parseInt(num1TextField.getText()) + Integer.parseInt(num2TextField.getText()))+"");
        }
        else if(operationsComboBox.getItemAt(operationsComboBox.getSelectedIndex()).equals("-")){
            resultLabel.setText((Integer.parseInt(num1TextField.getText()) - Integer.parseInt(num2TextField.getText()))+"");
        }
        else if(operationsComboBox.getItemAt(operationsComboBox.getSelectedIndex()).equals("*")){
            resultLabel.setText((Integer.parseInt(num1TextField.getText()) * Integer.parseInt(num2TextField.getText()))+"");
        }
        else{
            resultLabel.setText((Integer.parseInt(num1TextField.getText()) / Integer.parseInt(num2TextField.getText()))+"");
        }
    }                                          

