    private void textArea1KeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_jTextArea1KeyReleased
        // TODO add your handling code here:
        char ch = Character.toLowerCase(evt.getKeyChar());
        
        if (Character.isLetterOrDigit(ch) || Character.isWhitespace(ch)) {
            if (ch == 'a' || ch == 'e' || ch == 'i' || ch == 'o' || ch == 'u') {
                nonEditableTextArea.setText(nonEditableTextArea.getText() + Character.toUpperCase(ch));
            } else {
                nonEditableTextArea.setText(nonEditableTextArea.getText() + ch);
            }
        }
    }//GEN-LAST:event_jTextArea1KeyReleased

